	'DisplayName','max(g1,g3)');
set(plot1(4),'LineWidth',3,'LineStyle','-.','Color',[0 1 1],...
	'DisplayName','min(g1,g3)');

% Create xlabel
xlabel('X1','FontSize',14);

% Create ylabel
ylabel('X2','FontSize',14);
set(axes1,'XLim',[-6 6]);
set(axes1,'YLim',[-6 6]);
% Create title
title('User Case #3','FontSize',14);

% Create legend
legend(axes1,'show');

